let tray = global.get('tray');
let command = msg.payload.split(" ")[0];
let commandData = msg.payload.split(" ")[1];
let newData = false;
let args = "";
let topic = "newData";
switch(command) 
{
  case "*C802":
    global.set('settingInProgress',false);
    topic = "newSetting";
    newData = true;
    break;
  case "=V802":
    args = commandData.split(";");
    tray.speed.value = Number(args[0]);
    tray.status.value = 2;
    if (args[1] == "0400") tray.status.value = 0;
    if (args[1] == "047A") tray.status.value = 1;
    break;
  case "=V808":
    args = commandData.split(";");
    tray.pumpTemp.value = Number(args[0]);
    break;
  case "=V809":
    args = commandData.split(";");
    tray.power.value = Number(args[2]) / 10;
    break;
  case "=V810":
    args = commandData.split(";");
    tray.hours.value = Number(args[0]);
    break;
  case "=V811":
    args = commandData.split(";");
    tray.cycles.value = Number(args[0]);
    tray.watchdog.value = tray.watchdog.value + 1;
    if (tray.watchdog.value > 32760) tray.watchdog.value = 0;
    newData = true;
    break;
  default:
    // code block
}
return  {topic:topic, payload:newData}

